var otgblistImageSelector;
jQuery(document).ready(function(a){a("#otgblist_image_button").click(function(b){b.preventDefault();otgblistImageSelector=wp.media({title:"Upload Image",multiple:!1}).open().on("select",function(b){b=otgblistImageSelector.state().get("selection").first();a("#otgblist_image_id").val(b.toJSON().id);a("#otgblist_listing_image").attr("src",b.attributes.sizes.thumbnail.url).show(150);a(".media-modal-close").click()})});jQuery("#bulk-action-selector-top").change(function(b){jQuery("#bulk-action-selector-bottom").val(a("#bulk-action-selector-top").val());otgblistRenameElements()});
jQuery("#bulk-action-selector-bottom").change(function(b){jQuery("#bulk-action-selector-top").val(a("#bulk-action-selector-bottom").val());otgblistRenameElements()});jQuery("#cb-select-all-1").change(function(a){jQuery("#cb-select-all-1").prop("checked")?jQuery(".otgblist_list_checkbox").prop("checked",!0):jQuery(".otgblist_list_checkbox").prop("checked",!1)})});
function otgblistShowAll(a){jQuery(".otgblist_region_buttons button").removeClass("otgblist_active");jQuery(".otgblist_cat_buttons button").removeClass("otgblist_active");jQuery("#"+a).addClass("otgblist_active");jQuery(".otgblist_box").show(500)}
function otgblistRegionSelect(a,b){jQuery(".otgblist_region_buttons button").removeClass("otgblist_active");jQuery(".otgblist_cat_buttons button").removeClass("otgblist_active");jQuery("#"+b).addClass("otgblist_active");jQuery(".otgblist_r"+a).show(500);jQuery('.otgblist_box:not(".otgblist_r'+a+'")').hide(500)}
function otgblistCatSelect(a,b){jQuery(".otgblist_region_buttons button").removeClass("otgblist_active");jQuery(".otgblist_cat_buttons button").removeClass("otgblist_active");jQuery("#"+b).addClass("otgblist_active");jQuery(".otgblist_c"+a).show(500);jQuery('.otgblist_box:not(".otgblist_c'+a+'")').hide(500)}
function otgblistRenameElements(){"rename"==jQuery("#bulk-action-selector-top").val()?(jQuery(".otgblist_rename_off").hide(50),jQuery(".otgblist_rename_on").show(50)):(jQuery(".otgblist_rename_off").show(50),jQuery(".otgblist_rename_on").hide(50))}function otgblistRenameEntered(a){jQuery("#bulk_action_list_"+a).prop("checked",!0)};